import React from 'react'
import Fire from './fire-logo.svg';
import FireIntro from './california.jpg';
import Glacier from './glacier.jpg';
import ForestFire from './wildfire.jpg';

export const Nav = () => {
    return(
        <div id = "main-menu">
            <div id = "logo">
                <img src={Fire} className="fire-logo" alt="fire logo"/>
            </div>
            <div id="nav">
                <ul>
                    <li><a href = "#home">Home</a></li>
                    <li><a href = "#history">History</a></li>
                    <li><a href = "#prediction">Prediction</a></li>
                    <li><a href = "#prevention">Prevention</a></li>
                    <li><a href = "#community">Community</a></li>
                    <li><a href = "#about">About</a></li>
                </ul> 
            </div>
        </div>
    )
}

export const Intro = () => {
    return(
        <div id = "overlay">
            <div>
                <img src={FireIntro} id="fire-intro" alt = "fire intro"/>
                <div id = "title">WILDFIRES AND CLIMATE CHANGE</div>
            </div>
        </div>
    )
}

export const Descriptions = () => {
    return(
        <div id = "desc">
            <div id = "climate-change">
                <img src={Glacier} id="glacier" alt = "climate change"/>
                <div id = "glacier-text">[insert climate change text]</div>
            </div>
            <div id = "forest-fire">
                <img src={ForestFire} id="fire" alt = "forst fires"/>
                <div id = "fire-text">[insert forest fire text]</div>
            </div>
        </div>
    )
}
