import React, {useState, useEffect} from 'react';
import "react-responsive-carousel/lib/styles/carousel.min.css"; 
import { Carousel } from 'react-responsive-carousel';

const axios = require('axios');

export const History = () => {
    const [data,setData]=useState([]);

    /* set state data */
    useEffect(()=>{
        /* construct url queries */
        const url = 'http://localhost:3001/fires?Counties=';
        const arr = ["Sonoma", "Butte", "Napa"];
        const queries = arr.map(county => axios.get(url+county));

        let temp = [];
        const getData = () => {
            axios.all(queries)
            .then(resArr => {
                temp = resArr.map(resArr => resArr.data);
                setData(temp);
            });
        };
        getData();
    },[]);

    const getFireData = (county) => {
        console.log(county);
        let fireData = {
            "numFires": 0,
            "acresBurned" : 0
        };
        county.forEach(fire => {
            if(fire["ArchiveYear"] >= 2015)
                fireData.numFires++;
                fireData.acresBurned += fire["AcresBurned"];
        });
        return fireData;
    }

    console.log(data);

    return( 
       <Carousel>
           <div>
                <p class="carousel-text">{getFireData(data[0])[0]}</p>
            </div>
            <div>
                <p class="carousel-text"></p>    
            </div>
            <div>
                <p class="carousel-text"></p>
            </div>
       </Carousel>
    );
}
