import React, {useState, useEffect} from 'react';
import "react-responsive-carousel/lib/styles/carousel.min.css"; 
import { Carousel } from 'react-responsive-carousel';

const axios = require('axios');

export const History = () => {
    const [data,setData]=useState([]);
    const [carousel,setCarousel]=useState(false);

    const getData = () => {
        const url = 'http://localhost:3001/fires?Counties=';
        const arr = ["Sonoma", "Butte", "Napa"];
        const queries = arr.map(county => axios.get(url+county));
        let temp = [];

        axios.all(queries)
        .then(resArr => {
            temp = resArr.map(resArr => resArr.data);
            setData(temp);
        });
    };

    componentDidMount(()=>{
        getData();
    },[]);

    function getFireData(county) {
        let fireData = {
            "numFires": 0,
            "acresBurned": 0 
        };
        county.forEach(fire => {
            if(fire["ArchiveYear"] >= 2015) {
                fireData.numFires++;
                fireData.acresBurned += fire["AcresBurned"];
            }
        });
        return fireData;
    }

    function showFireData(county) {
        let fireData = getFireData(county);
        const numFires = fireData.numFires;
        const acresBurned = fireData.acresBurned;
        return( 
            <div>
                <p class="carousel-text">{"Number of Fires: " + numFires}</p>
                <p class="carousel-text">{"Acres Burned: " + acresBurned}</p>
            </div>
        );
    }
    console.log(data);

    return( 
       <Carousel>
           {showFireData(data[0])}
           {showFireData(data[1])}
           {showFireData(data[2])}
       </Carousel>
    );
}
