import React, {useState, useEffect} from 'react';
import "react-responsive-carousel/lib/styles/carousel.min.css"; 
import { Carousel } from 'react-responsive-carousel';
import { LineChart } from 'react-chartkick'
import 'chart.js'

const axios = require('axios');

export const History = () => {
    const [data,setData]=useState([]);
    const [carousel,setCarousel]=useState(false);

    const getData = () => {
        const url = 'http://localhost:3001/';
        const firesUrl = url + 'fires?Counties=';
        const sonomaUrl = url + 'sonoma_year';
        const counties = ["Sonoma", "Butte", "Napa"];
        const queries = counties.map(county => axios.get(firesUrl+county));
        queries.push(axios.get(sonomaUrl));
        let temp = [];

        axios.all(queries)
        .then(resArr => {
            temp = resArr.map(resArr => resArr.data);
            setData(temp);
            setCarousel(true);
        });
    };

    useEffect(()=>{
        getData();
    },[]);

    console.log(data);

    function getFireData(county) {
        let fireData = {
            "numFires": 0,
            "acresBurned": 0 
        };
        county.forEach(fire => {
            if(fire["ArchiveYear"] >= 2015) {
                fireData.numFires++;
                fireData.acresBurned += fire["AcresBurned"];
            }
        });
        return fireData;
    }

    function showFireData(county) {
        let fireData = getFireData(county);
        const numFires = fireData.numFires;
        const acresBurned = fireData.acresBurned;
        return( 
            <div class="fire-data">
                <p>{numFires + " fires"}</p>
                <p>{acresBurned + " acres burned"}</p>
                <p>since 2015</p>
            </div>
        );
    }

    function getWeatherData(county) {
        let data = [[], []];
        let prevCity = "";
        let currCity = "";
        let currIndex =  -1;
        county.forEach(obj => {
            let currCity = obj["NAME"];
            let dateProp = obj["DATE"];
            let maxtemp = obj["EMXT"];
            let precip = obj["PRCP"];
            if(prevCity.localeCompare(currCity) != 0) {
                let tempObj  = {"name": "", "data": {}};
                let precipObj  = {"name": "", "data": {}};
                tempObj.name = currCity;
                precipObj.name = currCity;
                tempObj.data = {[dateProp]: maxtemp};
                precipObj.data = {[dateProp]: precip}
                data[0].push(tempObj);
                data[1].push(precipObj);
                currIndex++;
            }
            else {
                data[0][currIndex].data.[dateProp] = maxtemp;
                data[1][currIndex].data.[dateProp] = precip;
            }
            prevCity = currCity;
        });
        return data;
    }

    return( 
        <>
        {carousel && (
            <>
                <Carousel>
                    <div>
                        {showFireData(data[0])} 
                        <LineChart id = "temp-chart" 
                        data = {getWeatherData(data[3])[0]} 
                        legend={false} 
                        min = {70} max = {115}
                        width = "25%" height = "100%"
                        />
                    </div>
                    {showFireData(data[0])}
                    {showFireData(data[1])}
                </Carousel>
            </>
        )}
        </>
    );
}
